import * as R from 'ramda'

export const getPokemonById = (state, id) => R.prop(id, state.pokemons)

export const getPokemons = state => {
  const applySearch = item => R.contains(
    state.pokemonsPage.search,
    R.prop('name', item)
  )
  const pokemons = R.compose(
    R.filter(applySearch),
    R.map(id => getPokemonById(state, id))
  )(state.pokemonsPage.ids)

  return pokemons
}

export const getRenderedPokemonsLength = state => R.length(state.pokemonsPage.ids)

